import pygame

pygame.init()

WIDTH = 300
HEIGHT = 300

screen = pygame.display.set_mode((WIDTH, HEIGHT))
pygame.display.set_caption('rip of dino game')

# i am the evil master 
# i am the evil master 
# i am the evil master 
# i am the evil master 


running = True
while running:
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            running = False

    screen.fill((225, 0, 147))

    pygame.draw.rect(screen, (0, 125, 0), (0, 200, WIDTH, HEIGHT))

    pygame.draw.rect(screen, (0, 225, 225), (50, 150, 50, 50))

    pygame.draw.polygon(screen, (255, 255, 255), [(200, 200), (225, 150), (250, 200)])

    pygame.display.update()